commits.content:
.container {
  background-color: #f9f9f9;

  .header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 20px;
    padding: 10px;
    background-color: #eae6e66d;
    // margin-bottom: 10px;

    .location {
      flex: 2;
    }

    .location-card {
      width: 70%;
      height: 125px;
      padding: 10px;
      justify-content: center;
      background-color: #89c6f95f;
      border-radius: 8px;
      box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
      color: black;

      p mat-icon {
        vertical-align: middle;
      }

      .location-name {
        font-weight: bold;
        margin: 0 0 5px;
      }

      .location-address {
        font-size: 14px;
        margin: 0;
      }
    }

    .header-section1 {
      flex: 3;
      text-align:center;

      h6 {
        font-size: 20px;
        margin-bottom: 20px;
        color: #424242;
      }

      .typewriter-animation {
        display: inline-block;
        overflow: hidden;
        color: rgb(40, 112, 188);
        animation: typing 3s steps(40) 1s infinite, blink 0.75s step-end infinite;
      }

      @keyframes typing {
        from {
          width: 0;
        }
        to {
          width: 100%;
        }
      }

      @keyframes blink {
        50% {
          border-color: transparent;
        }
      }


      .search-bar {
        width: 100%;
      }

    }

    .header-section2 {
      flex: 2;
      text-align: right;

      .image {
        img {
          width: 70%;
          height: auto;
          border-radius: 8px;
          box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
        }
      }
    }
  }


  .doctor-list {
    h6 {
      padding: 10px;
      font-size: 20px;
      margin-bottom: 20px;
      color: #424242;
    }

    .card-container {
      display: flex;
      justify-content: space-between;
      width: 100%;
      align-items:center;
    }

    .doctor-info {
      flex: 2;
      padding: 10px;
    }

    .actions, .consultation-fee {
      flex: 1;
      padding: 10px;
    }

    .doctor-card {
      display: flex;
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
      margin-top: 10px;
      margin-bottom: 10px;
      padding: 10px;

      .doctor-info {
        display: flex;
        align-items: center;

        .doctor-image {
          width: 80px;
          height: 80px;
          border-radius: 50%;
          margin-right: 20px;
          object-fit: cover;
          border: 2px solid #ddd;
        }

        .details {
          h4 {
            margin: 0;
            font-size: 16px;
          }

          p {
            margin: 2px 0;
            color: #757575;
          }
        }
      }

      .consultation-fee {
        display: flex;
        flex-direction: column;
        align-items:flex-end;
        padding: 0;
        gap: 0;
        margin-right: 20px;
      }

      .actions {
        display: flex;
        flex-direction: column;

        button {
          width: 190px;
          margin-bottom: 10px;
          font-size: 10px;
        }
      }
    }

    .view-more {
      text-align: center;

      button {
        font-weight: normal;
      }
    }
  }
}


