.ngcard-container {
  display: flex;
  justify-content: space-between;
  width: 100%;
  align-items:center;
}

.ngdoctor-info {
  flex: 1.2;
  padding: 10px;
}

.ngactions, .ngconsultation-fee {
  flex: 1;
  padding: 10px;
}


.ngdoctor-list {
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #f9f9f9;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);

  .ngcard-container {
    width: 100%;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 10px;
    padding: 0px;
  }

  .ngdoctor-info {
    display: flex;
    align-items: center;

    .ngdoctor-image {
      width: 80px;
      height: 80px;
      border-radius: 50%;
      margin-right: 20px;
      margin-left: 20px;
      object-fit: cover;
      border: 2px solid #ddd;
    }

    .ngdetails {
      h4 {
        margin: 0;
