+    padding: 5px;
+    font-size: 24px;
+    color: white;
+  }
+
+  .location-text {
+    font-size: 12px;
+    color: white;
+    line-height: 1.2;
+    // white-space: normal;
+    // word-wrap: break-word;
+    max-width: 300px;
+  }
+
+  .searchbox {
+    flex: 1;
+    font-size: 16px;
+    margin-top: 8px;
+    border-radius: 4px;
+    transition: box-shadow 0.3s ease;
+    width: 90%;
+
+    &:focus {
+      outline: none;
+      border-color: #007bff;
+    }
+  }
+}
