import { Component } from '@angular/core';
import { SharedModule } from '../../../shared/shared.module';
import { Router, RouterLink } from '@angular/router';
import { AuthService } from '../../../authentication/auth.service';
import { SidebarComponent } from '../sidebar/sidebar.component';

@Component({
  selector: 'app-myheader',
  standalone: true,
  imports: [SharedModule, RouterLink],
  templateUrl: './myheader.component.html',
  styleUrl: './myheader.component.scss'
})
export class MyheaderComponent {

  isSidebarOpen = true;
  username: string = '';
  isSignedIn: boolean = false;

  constructor(private authService: AuthService, private router: Router, private sidebar: SidebarComponent) {
    // Get the authentication status
    this.isSignedIn = this.authService.isSignedIn();

    if (this.isSignedIn) {
      this.username = this.authService.getUsername();
    }
  }

  toggleSidebar() {
    this.isSidebarOpen = this.sidebar.isSidebarOpen;
  }

  logout(): void {
    this.authService.logout();
    this.router.navigate(['/']);
  }

  onAccountClick(): void {
    if (!this.isSignedIn) {
      this.router.navigate(['/signin']);
    }
  }

}
