import { Component } from '@angular/core';
import { SharedModule } from '../../../shared/shared.module';

@Component({
  selector: 'app-myorders',
  standalone: true,
  imports: [SharedModule],
  templateUrl: './myorders.component.html',
  styleUrl: './myorders.component.scss'
})
export class MyordersComponent {
  selectedFilter: string = 'all'; // Default filter
  orders = [
    {
      id: '35184812',
      date: '28 October 2024',
      type: 'Virtual Consultation',
      status: 'Confirmed',
      appointment: '25 Jan 2025, 12:15 PM',
      patient: 'Rajesh',
    },
    {
      id: '35184813',
      date: '15 December 2024',
      type: 'Lab Test',
      status: 'Pending',
      appointment: '27 Jan 2025, 10:30 AM',
      patient: 'Suresh',
    },
  ];

  filteredOrders = [...this.orders];

  filterOrders() {
    if (this.selectedFilter === 'all') {
      this.filteredOrders = [...this.orders];
    } else {
      this.filteredOrders = this.orders.filter(
        (order) => order.type.toLowerCase() === this.selectedFilter
      );
    }
  }

  refreshOrder(orderId: string) {
    console.log(`Refresh order with ID: ${orderId}`);
    // Add logic to refresh the order
  }

  cancelOrder(orderId: string) {
    console.log(`Cancel order with ID: ${orderId}`);
    // Add logic to cancel the order
  }

  getCalendarDate(appointment: string): string {
    return appointment.split(',')[0]; // Extract the date
  }

  getCalendarDay(appointment: string): string {
    return 'Sat, Jan 25'; // Replace with actual day logic if needed
  }

  getCalendarTime(appointment: string): string {
    return appointment.split(',')[1].trim(); // Extract the time
  }
}
