.container {
  display: flex;
  font-family: Arial, sans-serif;
}

/* Toolbar Styles */
.logo-section {
  display: flex;
  align-items: center;
}

.logo {
  height: 50px;
  margin-right: 10px;
}

.spacer {
  flex: 1;
}

.account-button {
  color: white;
  :hover{
    transform: scale(1.09);
  }

}

.sidebar {
  width: 13%;
  height: 100vh;
  background-color: rgba(230, 233, 236, 0.352);
  color: black;
  padding: 0px;
  position: fixed;
  transition: transform 0.3s ease;
}

.sidebar.closed {
  transform: translateX(-100%);
}

.sidebar legend {
  font-size: 1.1em;
  font-weight: bold;
  font-family:cursive;
  padding: 15px;
  margin-bottom: 10px;
}

.sidebar ul {
  list-style-type: none;
  font-family:cursive;
  padding: 10px;
}

.sidebar ul li {
  margin: 10px 0;
}

.sidebar ul li a {
  color: black;
  text-decoration: none;
  display: block;
  padding: 10px;
  transition: background-color 0.3s ease;
  border-radius: 20px;
}

.sidebar ul li a:hover {
  background-color: #acb8c4;
  border-radius: 20px;
}

/* Main content styling */
.content {
  width: 88%;
  padding: 20px;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.header legend {
  font-size: 20px;
  font-weight: bold;
}

.filter-dropdown {
  width: 150px;
  height: 60px;
}

mat-card {
  padding: 10px;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.1);
}

.order-summary {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.order-details {
  margin-top: 20px;
  line-height: 1.6;
}

.order-actions {
  margin-top: 20px;
}

.footer {
  text-align: center;
  margin-top: 20px;
}

.footer button {
  margin-top: 10px;
}
