/* You can add global styles to this file, and also import other style files */

html, body { height: 100%; }
body { margin: 0; font-family: Roboto, "Helvetica Neue", sans-serif; }

/* Import a clean and modern font */
// @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;500;700&display=swap');

@font-face {
  font-family: 'Roboto';
  src: url('/fonts/Roboto/Roboto-Medium.ttf') format('truetype');
  // font-weight: 900;
  font-style: normal;
}


/* Set global font */
body {
  font-family: 'Roboto', sans-serif;
  font-size: 14px;
  color: #333; /* Dark gray for readability */
  line-height: 1.6;
}

/* Headings */
h1, h2, h3, h4, h5, h6 {
  font-family: 'Roboto', sans-serif;
  font-weight: 700;
  color: #002855; /* Blue for headings */
  margin: 0 0 10px;
}

h1 {
  font-size: 32px;
}

h2 {
  font-size: 24px;
}

h3 {
  font-size: 20px;
}

h4 {
  font-size: 18px;
}

h5, h6 {
  font-size: 16px;
}

/* Paragraphs */
p {
  font-size: 14px;
  color: #555; /* Slightly lighter gray for body text */
  margin: 0 0 10px;
}

/* Links */
a {
  color: #007bff; /* Blue for links */
  text-decoration: none;
}

a:hover {
  color: #0056b3;
  text-decoration: underline;
}

/* Buttons */
button, mat-button {
  font-size: 14px;
  font-weight: 500;
  text-transform: none; /* Avoid all-caps */
  padding: 8px 16px;
}

button:hover, mat-button:hover {
  background-color: #e0f7fa; /* Light blue hover effect */
}

/* Input fields */
mat-form-field {
  font-size: 14px;
  color: #333;
}

mat-input-element {
  font-size: 14px;
}

/* Small text (for captions, footnotes) */
.small-text {
  font-size: 12px;
  color: #888;
}

/* Section titles */
.section-title {
  font-size: 20px;
  font-weight: 700;
  color: #002855;
  margin-bottom: 10px;
}

/* Card text */
.mat-card-title {
  font-size: 16px;
  font-weight: 700;
  color: #333;
}

.mat-card-subtitle {
  font-size: 14px;
  color: #555;
}

.mat-option {
  background-color: #ffffff !important;
}

::ng-deep .mat-tooltip {
  background: rgba(0, 0, 0, 0.6) !important; /* Semi-transparent black */
  border-radius: 4px; /* Makes it slightly rectangular */
  padding: 5px 10px; /* Adjust padding for horizontal rectangle */
  text-align: center;
  max-width: 300px; /* Restrict width */
  white-space: nowrap; /* Prevents wrapping */
  font-size: 14px;
  color: white; /* Text color */
}
